import questions from "./data";
import { useState } from "react";
export default function App() {
  return (
    <div>
      <FlashCards />
    </div>
  );
}

function FlashCards() {
  const [answer, setanswer] = useState(questions);

  function handleclick() {
    setanswer(questions.map((each) => each.answer));
  }
  return (
    <div className="flashcards" onClick={handleclick}>
      {questions.map((each) => (
        <div>{answer}</div>
      ))}
    </div>
  );
}
