import questions from "./data";
import { useState } from "react";
export default function App() {
  return (
    <div>
      <FlashCards />
    </div>
  );
}

function FlashCards() {
  const [answer, setanswer] = useState("just testing out");

  function handleclick() {
    setanswer(" jus");
  }
  return (
    <div className="flashcards" onClick={handleclick}>
      {questions.map((each) => (
        <div onClick={handleclick}>{answer}</div>
      ))}
    </div>
  );
}
